targets.expr:
(
    (
        sum(rate(linera_server_request_error{validator=~"$validator"}[1m])) by (method_name, pod)
    ) / (
        (
            (
                sum(rate(linera_server_request_success{validator=~"$validator"}[1m])) by (method_name, pod)
            ) + (
                sum(rate(linera_server_request_error{validator=~"$validator"}[1m])) by (method_name, pod)
            )
        )
    )
) * 100
